mapper.select:
  	SELECT
		p.*, u.nickname,
		r.role_code,
		r.role_name,
		o.create_time AS order_time
	FROM
		account_personal p
	LEFT JOIN trade_orders o ON p.order_id = o.id
	LEFT JOIN user_extends ue ON o.extend_id = ue.extend_id
	LEFT JOIN `user` u ON p.bind_user_id = u.id
	LEFT JOIN role r ON p.role_id = r.role_id
	WHERE 
		p.type=7
		
			and r.role_code IN 
			
			
				#{item}
			
		
		
			and u.id = #{user_id}
		
		
			and p.order_id = #{order_id}
		
		
			and o.create_time >= #{start_time}
		
		
			and o.create_time <= #{end_time}
		
		
  			and ue.province=#{provinceName} 
  		  		
  		
  			and ue.city=#{cityName} 
  		
  		
  			and ue.county=#{countyName}

	select * from account_personal 
	
		order_id=#{orderId}  		   		
		
			AND
			type in 	
			  
          	#{itemType}  
      	  		
		
		
			AND bind_user_id=#{userId}
		
		
			AND role_id in
			  
          	#{item}

select ac.* from account_personal ac LEFT JOIN trade_orders o on(o.id=ac.order_id) LEFT JOIN user_extends a on(o.extend_id=a.extend_id)
ac.cust_id is not NULL AND o.create_time>=DATE_ADD(now(),INTERVAL #{orderTimeCond} MONTH)
AND o.contract_state=#{dealStateCond}
AND o.order_id LIKE concat(concat('%',#{condValue}),'%')
AND o.contract_no LIKE concat(concat('%',#{condValue}),'%')
AND o.name LIKE concat(concat('%',#{condValue}),'%')
AND o.mobile LIKE concat(concat('%',#{condValue}),'%')
AND a.company_name LIKE concat(concat('%',#{condValue}),'%')
AND a.artificial_person_name LIKE concat(concat('%',#{condValue}),'%')
AND a.contact_phone LIKE concat(concat('%',#{condValue}),'%')
AND a.province=#{provinceName}
AND a.city=#{cityName}
AND a.county=#{countyName}
AND type in
#{itemType}
AND ac.bind_user_id=#{userId}
AND ac.role_id in
#{item}
AND a.extend_id in
#{item.cust_id}
and (0>1)
order by o.create_time DESC
